$(function(){

  $("#start").hover(function() { $(this).animate({textShadow: "#D67C00 0 0 20px"}); }, function() { $(this).animate({textShadow: "#D67C00 0 0 0"}); });
  $("#pause").hover(function() { $(this).animate({textShadow: "#D67C00 0 0 20px"}); }, function() { $(this).animate({textShadow: "#D67C00 0 0 0"}); });
  $("#reset").hover(function() { $(this).animate({textShadow: "#D67C00 0 0 20px"}); }, function() { $(this).animate({textShadow: "#D67C00 0 0 0"}); });

})